function cg = BPEAsymptoticDt(x, t, k, a1, a2, a12, b)
    cg = 0.2e1 * a12 * (k ^ 2) * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * (0.8e1 * a1 ^ 3 * a2 ^ 3 * exp(-0.2e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - (2 * b)) * a12 - a1 * a2 ^ 4 * exp(-(3 * k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) * a12 ^ 2 - 0.8e1 * a1 * a12 ^ 5 * exp(0.2e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (2 * b)) * a2 - a2 * a1 ^ 4 * exp((3 * k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) * a12 ^ 2 + a1 ^ 3 * a2 ^ 4 * exp(-(k * x) - 0.3e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - (3 * b)) + 0.6e1 * a1 ^ 2 * a2 ^ 3 * exp(-(k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) * a12 ^ 2 + a1 ^ 3 * a12 ^ 4 * exp(sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (3 * k * x) + b) - 0.6e1 * a1 ^ 2 * a12 ^ 4 * exp(sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (k * x) + b) * a2 - a1 * a12 ^ 6 * exp(0.3e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (k * x) + (3 * b)) + a2 ^ 3 * a1 ^ 4 * exp((k * x) - 0.3e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - (3 * b)) + 0.6e1 * a2 ^ 2 * a1 ^ 3 * exp((k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) * a12 ^ 2 - 0.6e1 * a2 ^ 2 * a12 ^ 4 * exp(-(k * x) + sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + b) * a1 + a2 ^ 3 * a12 ^ 4 * exp(-(3 * k * x) + sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + b) - a2 * a12 ^ 6 * exp(-(k * x) + 0.3e1 * sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (3 * b))) ./ ...
        (a1 ^ 2 * exp((k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) + 0.2e1 * a1 * a12 + a12 ^ 2 * exp(-(k * x) + sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + b)) .^ 2 ./ ...
        (a2 ^ 2 * exp(-(k * x) - sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t - b) + 0.2e1 * a12 * a2 + a12 ^ 2 * exp(sqrt(-(k ^ 2 * (-1 + k ^ 2))) * t + (k * x) + b)) .^ 2;
end