function [dv2,dv3,dv4,dv5,dv6,dv7,dv8,dv9,dv10,dv11,dv12]=calc_der_vc_rs(q,v,dv1,gamma1,gamma2,order)

if(order<4 && order>12)
    error('order value is wrong!');
end

dv2 = vpa(exp(2*q)*( gamma1 * v + gamma2 * v^2),50);

dv3 = vpa(0.2e1 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1),50);

dv4 = vpa(0.4e1 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.4e1 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2),50);
if(order == 4) dv5=0; dv6=0;  dv7=0; dv8=0; dv9=0; dv10=0;  dv11=0; dv12=0; return; end
    
dv5 = vpa(0.8e1 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.12e2 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.6e1 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3),50);
if(order == 5) dv6=0;  dv7=0; dv8=0;  dv9=0; dv10=0;  dv11=0; dv12=0; return; end

dv6 = vpa(0.16e2 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.32e2 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.24e2 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.8e1 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4),50);
if(order == 6)  dv7=0; dv8=0;  dv9=0; dv10=0;  dv11=0; dv12=0; return; end

dv7 = vpa(0.32e2 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.80e2 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.80e2 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.40e2 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.10e2 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5),50);
if(order == 7)  dv8=0;  dv9=0; dv10=0;  dv11=0; dv12=0; return; end

dv8 = vpa(0.64e2 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.192e3 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.240e3 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.160e3 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.60e2 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + 0.12e2 * exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5) + exp((2 * q)) * (gamma1 * dv6 + 0.20e2 * gamma2 * dv3 ^ 2 + 0.30e2 * gamma2 * dv2 * dv4 + 0.12e2 * gamma2 * dv1 * dv5 + 0.2e1 * gamma2 * v * dv6),50);
if(order == 8)   dv9=0; dv10=0;  dv11=0; dv12=0;  return; end

dv9 = vpa(0.128e3 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.448e3 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.672e3 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.560e3 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.280e3 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + 0.84e2 * exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5) + 0.14e2 * exp((2 * q)) * (gamma1 * dv6 + 0.20e2 * gamma2 * dv3 ^ 2 + 0.30e2 * gamma2 * dv2 * dv4 + 0.12e2 * gamma2 * dv1 * dv5 + 0.2e1 * gamma2 * v * dv6) + exp((2 * q)) * (gamma1 * dv7 + 0.70e2 * gamma2 * dv3 * dv4 + 0.42e2 * gamma2 * dv2 * dv5 + 0.14e2 * gamma2 * dv1 * dv6 + 0.2e1 * gamma2 * v * dv7),50);
if(order == 9)  dv10=0;  dv11=0; dv12=0;  return; end

dv10 = vpa(0.256e3 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.1024e4 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.1792e4 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.1792e4 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.1120e4 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + 0.448e3 * exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5) + 0.112e3 * exp((2 * q)) * (gamma1 * dv6 + 0.20e2 * gamma2 * dv3 ^ 2 + 0.30e2 * gamma2 * dv2 * dv4 + 0.12e2 * gamma2 * dv1 * dv5 + 0.2e1 * gamma2 * v * dv6) + 0.16e2 * exp((2 * q)) * (gamma1 * dv7 + 0.70e2 * gamma2 * dv3 * dv4 + 0.42e2 * gamma2 * dv2 * dv5 + 0.14e2 * gamma2 * dv1 * dv6 + 0.2e1 * gamma2 * v * dv7) + exp((2 * q)) * (gamma1 * dv8 + 0.70e2 * gamma2 * dv4 ^ 2 + 0.112e3 * gamma2 * dv3 * dv5 + 0.56e2 * gamma2 * dv2 * dv6 + 0.16e2 * gamma2 * dv1 * dv7 + 0.2e1 * gamma2 * v * dv8),50);
if(order == 10)  dv11=0; dv12=0;  return; end

dv11 = vpa(0.512e3 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.2304e4 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.4608e4 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.5376e4 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.4032e4 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + 0.2016e4 * exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5) + 0.672e3 * exp((2 * q)) * (gamma1 * dv6 + 0.20e2 * gamma2 * dv3 ^ 2 + 0.30e2 * gamma2 * dv2 * dv4 + 0.12e2 * gamma2 * dv1 * dv5 + 0.2e1 * gamma2 * v * dv6) + 0.144e3 * exp((2 * q)) * (gamma1 * dv7 + 0.70e2 * gamma2 * dv3 * dv4 + 0.42e2 * gamma2 * dv2 * dv5 + 0.14e2 * gamma2 * dv1 * dv6 + 0.2e1 * gamma2 * v * dv7) + 0.18e2 * exp((2 * q)) * (gamma1 * dv8 + 0.70e2 * gamma2 * dv4 ^ 2 + 0.112e3 * gamma2 * dv3 * dv5 + 0.56e2 * gamma2 * dv2 * dv6 + 0.16e2 * gamma2 * dv1 * dv7 + 0.2e1 * gamma2 * v * dv8) + exp((2 * q)) * (gamma1 * dv9 + 0.252e3 * gamma2 * dv4 * dv5 + 0.168e3 * gamma2 * dv3 * dv6 + 0.72e2 * gamma2 * dv2 * dv7 + 0.18e2 * gamma2 * dv1 * dv8 + 0.2e1 * gamma2 * v * dv9),50);
if(order == 11)  dv12=0;  return; end

dv12 = vpa(0.1024e4 * exp((2 * q)) * (gamma1 * v + gamma2 * v ^ 2) + 0.5120e4 * exp((2 * q)) * (gamma1 * dv1 + 0.2e1 * gamma2 * v * dv1) + 0.11520e5 * exp((2 * q)) * (gamma1 * dv2 + 0.2e1 * gamma2 * dv1 ^ 2 + 0.2e1 * gamma2 * v * dv2) + 0.15360e5 * exp((2 * q)) * (gamma1 * dv3 + 0.6e1 * gamma2 * dv1 * dv2 + 0.2e1 * gamma2 * v * dv3) + 0.13440e5 * exp((2 * q)) * (gamma1 * dv4 + 0.6e1 * gamma2 * dv2 ^ 2 + 0.8e1 * gamma2 * dv1 * dv3 + 0.2e1 * gamma2 * v * dv4) + 0.8064e4 * exp((2 * q)) * (gamma1 * dv5 + 0.20e2 * gamma2 * dv2 * dv3 + 0.10e2 * gamma2 * dv1 * dv4 + 0.2e1 * gamma2 * v * dv5) + 0.3360e4 * exp((2 * q)) * (gamma1 * dv6 + 0.20e2 * gamma2 * dv3 ^ 2 + 0.30e2 * gamma2 * dv2 * dv4 + 0.12e2 * gamma2 * dv1 * dv5 + 0.2e1 * gamma2 * v * dv6) + 0.960e3 * exp((2 * q)) * (gamma1 * dv7 + 0.70e2 * gamma2 * dv3 * dv4 + 0.42e2 * gamma2 * dv2 * dv5 + 0.14e2 * gamma2 * dv1 * dv6 + 0.2e1 * gamma2 * v * dv7) + 0.180e3 * exp((2 * q)) * (gamma1 * dv8 + 0.70e2 * gamma2 * dv4 ^ 2 + 0.112e3 * gamma2 * dv3 * dv5 + 0.56e2 * gamma2 * dv2 * dv6 + 0.16e2 * gamma2 * dv1 * dv7 + 0.2e1 * gamma2 * v * dv8) + 0.20e2 * exp((2 * q)) * (gamma1 * dv9 + 0.252e3 * gamma2 * dv4 * dv5 + 0.168e3 * gamma2 * dv3 * dv6 + 0.72e2 * gamma2 * dv2 * dv7 + 0.18e2 * gamma2 * dv1 * dv8 + 0.2e1 * gamma2 * v * dv9) + exp((2 * q)) * (gamma1 * dv10 + 0.252e3 * gamma2 * dv5 ^ 2 + 0.420e3 * gamma2 * dv4 * dv6 + 0.240e3 * gamma2 * dv3 * dv7 + 0.90e2 * gamma2 * dv2 * dv8 + 0.20e2 * gamma2 * dv1 * dv9 + 0.2e1 * gamma2 * v * dv10),50);

end
